import React from 'react';
import { Bar } from 'react-chartjs-2';
import {Select} from '@material-ui/core';

export default class BarGraph extends React.Component {
    render() {
        return (
            <>
            <span><div style={{float:"left", height: "250px",width: "500px", display: "flex", justifyContent: "space-between"  }}>
                <Bar
                    data={this.props.data}
                    options={{
                        legend:{
                            display: false
                        },
                       responsive: true,
                       title: {text: 'FUNCIÓN DISTRIBUCIÓN', display: true},
                       scales:{
                           yAxes:[
                               {
                                   ticks:{
                                        autoSkip: true,
                                        beginAtZero: true,
                                        min: 0,
                                   }
                               }
                           ]
                       }
                    }}
                />
            </div></span>

            <span><div style={{float:"right", width: "500px", display: "flex", justifyContent: "space-between"  }}>
            <Select
                    native
                    inputProps={{
                        name: 'age',
                        id: 'age-native-simple',
                    }}
                    >
                    <option aria-label="None" value="" />
                    <option value={10}>Ten</option>
                    <option value={20}>Twenty</option>
                    <option value={30}>Thirty</option>
                </Select>
                </div></span>
            </>

        );
    }
}
