import Distribution from './Distribution';
import React, {Component} from 'react'; 
import NumberField from '../Components/NumberField';

class Binomial extends Distribution {
    constructor () {
      super();
      this.name = "Binomial";
      this.state={
        p: 0,
        n: 0
      }
    }

    calcularMedia(){
      const result = this.n*this.p;
      return (<div> {result}</div>);
    }

    getAttributes(){
      return (<div>
        <NumberField label={"p"} handleChange={event =>{this.setState({p: event.target.value})}}/>
        <NumberField label={"n"} handleChange={event =>{this.setState({n: event.target.value})}}/>
      </div>
      )
    }
}

export default Binomial;
