import Distribution from './Distribution';
import React, {Component} from 'react'; 
import NumberField from '../Components/NumberField';

class Binomial extends Component {
    constructor () {
      super();
      this.changeN = this.changeN.bind(this);
      this.changeP = this.changeP.bind(this);

      this.state={
        name: "Binomial",
        p: 0,
        n: 0,
        media: 0,
        varianza: 0
      }
    }

    calcularTodo(){
      this.calcularMedia();
      this.calcularVarianza();
    }

    calcularMedia(){
      const result = this.n*this.p;
      this.setState({media: result})
    }

    calcularMedia(){
      const result = this.n*this.p;
      this.setState({varianza: result})
    }

    changeP (event){
      this.setState({p: event.target.value});
      this.calcularTodo();
    }

    changeN (event){
      this.setState({n: event.target.value});
      this.calcularTodo();
    }

    getAttributes(){
      return (<div>
        <NumberField label={"p"} handleChange={this.changeP}/>
        <NumberField label={"n"} handleChange={this.changeN}/>
      </div>
      )
    }

    render(){
      return(
        <div>
          <this.getAttributes/>
          <Distribution name={this.state.name} media={this.state.media} varianza={this.state.varianza}/>
        </div>
      )
    }
}

export default Binomial;
