import Distribution from './Distribution';
import React, {Component} from 'react'; 
import NumberField from '../Components/NumberField';

class Binomial extends Distribution {
    constructor () {
      super();

      this.state={
        name: "Binomial",
        p: 0,
        n: 0
      }

      this.changeP = this.changeP.bind(this);
      this.changeN = this.changeN.bind(this);
    }

    calcularMedia(){
      const result = this.n*this.p;
      return (<div> {result}</div>);
    }

    changeP (event){
      this.setState({p: event.target.value});
    }

    changeN (event){
      this.setState({n: event.target.value});
    }

    getAttributes(){
      return (<div>
        <NumberField label={"p"} handleChange={(e) => this.changeP(e)}/>
        <NumberField label={"n"} handleChange={(e) => this.changeN(e)}/>
      </div>
      )
    }
}

export default Binomial;
