import Distribution from './Distribution';
import React, {Component} from 'react'; 
import NumberField from '../Components/NumberField';
import LineGraph from '../Components/LineGraph'

class Binomial extends Component {
    constructor (props) {
      super(props);
      this.changeN = this.changeN.bind(this);
      this.changeP = this.changeP.bind(this);

      this.state={
        name: "Binomial",
        p: 0,
        n: 0,
        media: 0,
        varianza: 0,
        desviacion: 0,
        nPoints: 50,
        labels: [],
        data: []
      }
    }

    componentDidUpdate(_prevProps, prevState) {
      if (prevState.p !== this.state.p || prevState.n !== this.state.n) {
        this.calcularTodo();
      }
    }

    calcularTodo() {
      this.calcularMedia();
      this.calcularVarianza();
      this.calcularFuncion();
    }

    calcularMedia() {
      let result = Number(this.state.n) * Number(this.state.p);  
      this.setState({ media: result });
    }

    calcularVarianza() {
      let result = Number(this.state.n) * Number(this.state.p) * (1-this.state.p);  
      this.setState({ media: result });
    }

    calcularDesviacion(state){
      let result = (state.n * state.p * (1-state.p)) ** (1/2);

      this.setState({ desviacion: result });
    }

    calcularFuncion(){
      this.state.data = [];
      this.state.labels = [];

      for (var i = 0; i < state.nPoints; i++) {
        state.labels.push(i);

        let prob = this.calcularProbabilidad(state, i);
        state.data.push(prob);
      } 
    }


    calcularProbabilidad(state, x){
      let result = this.choose(state.n, x) * state.p**x * (1-state.p)**(state.n-x);
    }

    factorial(n) {
      if (n === 1) {
        return 1;
      }

      return n * this.factorial(n - 1);
    }

    choose(a,b){
      let result = (this.factorial(a))/(this.factorial(b) * this.factorial(a-b));
    }

    render(){
      return(
        <div>
          <NumberField label={"n"} min={0} max={9999999} handleChange={this.changeN}/>
          <NumberField label={"p"} min={0} max={1} handleChange={this.changeP}/>
          <LineGraph _labels={['a', 'b']} _data= {[1,2]}/>
          <Distribution name={this.state.name} 
            media={this.state.media} 
            varianza={this.state.varianza}
            desviacion={this.state.desviacion}
          />
        </div>
      )
    }

    changeP = async function(event) {
      await this.setState({ p: Number(event.target.value) });
    };

    changeN = async function(event) {
      await this.setState({ n: Number(event.target.value) });
    };
}

export default Binomial;
